SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_exec_req_nao_util ( nr_seq_segurado_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Excluir as execuções das requisições que não foram utilizadas.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

delete from pls_execucao_req_item
where	nr_sequencia in (SELECT w.nr_seq_item_exec
			 from 	w_pls_itens_benef_exec w
			 where	w.nr_seq_segurado =  nr_seq_segurado_p
			 and	w.ie_tipo_consulta = 'LE');

delete	FROM pls_execucao_requisicao
where	nr_sequencia in (SELECT w.nr_seq_execucao
			 from 	w_pls_itens_benef_exec w
			 where	w.nr_seq_segurado =  nr_seq_segurado_p
			 and	w.ie_tipo_consulta = 'LE');

/* Limpar lista dos itens utilizados na execução da requisição */

CALL pls_delete_w_itens_execucao(null, nr_seq_segurado_p, 'LE');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_exec_req_nao_util ( nr_seq_segurado_p bigint, nm_usuario_p text) FROM PUBLIC;

