SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_faturamento_rec ( nr_seq_fatura_rec_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Excluir a fatura selecionada, do lote de cancelamento de faturamento.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_faturas_lote_w	bigint;
nr_seq_lote_w		bigint;


BEGIN
if (nr_seq_fatura_rec_p IS NOT NULL AND nr_seq_fatura_rec_p::text <> '') then
	-- verifica quantas faturas existem no lote e retorna a sequência do lote
	select	b.nr_seq_lote,
		count(b.nr_sequencia)
	into STRICT	nr_seq_lote_w,
		qt_faturas_lote_w
	from	pls_cancel_rec_fatura a,
		pls_cancel_rec_fatura b
	where	a.nr_sequencia	= nr_seq_fatura_rec_p
	and	b.nr_seq_lote	= a.nr_seq_lote
	group by b.nr_seq_lote;

	delete
	from	pls_cancel_rec_fat_incons
	where	nr_seq_fatura_rec = nr_seq_fatura_rec_p;

	delete
	from	pls_cancel_rec_fatura
	where	nr_sequencia = nr_seq_fatura_rec_p;

	-- se acaso não existirem mais faturas para o lote, coloca o mesmo como provisório
	if (qt_faturas_lote_w = 1) then
		CALL pls_atualizar_stat_lote_canc(	nr_seq_lote_w,
						'P',
						'F',
						nm_usuario_p);
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_faturamento_rec ( nr_seq_fatura_rec_p bigint, nm_usuario_p text) FROM PUBLIC;

