SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_item_auditoria ( nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_motivo_exclusao_w		bigint;
nr_seq_auditoria_w		bigint;
nr_seq_requisicao_w		bigint;
nr_seq_execucao_w		bigint;
ie_status_solicitacao_w		varchar(2);
ie_origem_auditoria_w		varchar(2);
nr_seq_guia_w			pls_guia_plano.nr_sequencia%type;
ie_tipo_processo_w		varchar(2);
ie_tipo_intercambio_w		varchar(2);
ie_origem_solic_w		varchar(2);


BEGIN

nr_motivo_exclusao_w	:= obter_valor_param_usuario(1270, 1, Obter_Perfil_Ativo, nm_usuario_p, 0);

if (coalesce(nr_motivo_exclusao_w::text, '') = '') then
	--Não existe motivo de exclusão informado no parâmetro[1].
	CALL wheb_mensagem_pck.exibir_mensagem_abort(203365);
end if;

begin
	select	nr_seq_auditoria,
		ie_status_solicitacao
	into STRICT	nr_seq_auditoria_w,
		ie_status_solicitacao_w
	from	pls_auditoria_item
	where	nr_sequencia	= nr_seq_item_p;
exception
when others then
	nr_seq_auditoria_w	:= null;
	ie_status_solicitacao_w	:= null;
end;

if (nr_seq_auditoria_w IS NOT NULL AND nr_seq_auditoria_w::text <> '') then
	begin
		select	nr_seq_requisicao,
			nr_seq_execucao,
			nr_seq_guia
		into STRICT	nr_seq_requisicao_w,
			nr_seq_execucao_w,
			nr_seq_guia_w
		from	pls_auditoria
		where	nr_sequencia	= nr_seq_auditoria_w;
	exception
	when others then
		nr_seq_requisicao_w	:= null;
		nr_seq_execucao_w	:= null;
	end;

	if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
		ie_origem_auditoria_w	:= 'R';
	elsif (nr_seq_execucao_w IS NOT NULL AND nr_seq_execucao_w::text <> '') then
		ie_origem_auditoria_w	:= 'E';
	end if;

	if	(ie_origem_auditoria_w	= 'E' AND ie_status_solicitacao_w	= 'A') or
		((ie_origem_auditoria_w	= 'R') and (substr(pls_obter_item_req_executado(nr_seq_item_p, ie_origem_auditoria_w),1,255)	= 'S'))	then
		--O item não pode ser excluído pois já foi executado. Favor verificar!
		CALL wheb_mensagem_pck.exibir_mensagem_abort(203364);
	end if;

	if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then
		begin
			select	ie_tipo_processo,
				ie_tipo_intercambio,
				ie_origem_solic
			into STRICT	ie_tipo_processo_w,
				ie_tipo_intercambio_w,
				ie_origem_solic_w
			from	pls_guia_plano
			where	nr_sequencia	= nr_seq_guia_w;
		exception
		when others then
			ie_tipo_processo_w	:= null;
			ie_tipo_intercambio_w	:= null;
		end;

		if (ie_tipo_processo_w	= 'I') and (ie_tipo_intercambio_w	= 'E') then
			-- Você não pode realizar esta ação em uma guia recebida via intercâmbio!
			CALL wheb_mensagem_pck.exibir_mensagem_abort(271396);
		elsif (ie_origem_solic_w	= 'E') then
			-- Você não pode realizar esta ação em uma guia recebida eletronicamente!
			CALL wheb_mensagem_pck.exibir_mensagem_abort(676204);
		end if;
	end if;
	if (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
		begin
			select	ie_tipo_processo,
				ie_tipo_intercambio,
				ie_origem_solic
			into STRICT	ie_tipo_processo_w,
				ie_tipo_intercambio_w,
				ie_origem_solic_w
			from	pls_requisicao
			where	nr_sequencia	= nr_seq_requisicao_w;
		exception
		when others then
			ie_tipo_processo_w	:= null;
			ie_tipo_intercambio_w	:= null;
		end;

		if (ie_tipo_processo_w	= 'I') and (ie_tipo_intercambio_w	= 'E') then
			-- Você não pode realizar esta ação em uma requisição recebida via intercâmbio!
			CALL wheb_mensagem_pck.exibir_mensagem_abort(358870);
		elsif (ie_origem_solic_w	= 'E') then
			-- Você não pode realizar esta ação em uma requisição recebida eletronicamente!
			CALL wheb_mensagem_pck.exibir_mensagem_abort(676205);
		end if;
	end if;

end if;

update	pls_auditoria_item
set	ie_acao_auditor	= 'E',
	ie_status	= CASE WHEN ie_origem_auditoria_w='R' THEN  'C'  ELSE 'N' END ,
	nr_seq_motivo_exc = nr_motivo_exclusao_w,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia = nr_seq_item_p;

CALL pls_gerar_hist_log_req(nr_seq_item_p,2,nm_usuario_p,0,0,0,0);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
