CREATE OR REPLACE PROCEDURE pls_excluir_item_requisicao ( nr_seq_requisicao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_consistir_req_p text) AS $body$
DECLARE

		 
qt_registro_w		integer := 0;
nr_seq_anexo_w		pls_lote_anexo_guias_aut.nr_sequencia%type;	
 

BEGIN 
 
if (coalesce(ie_consistir_req_p,'S') = 'S') then 
	CALL pls_req_desfazer_consistir(nr_seq_requisicao_p,nm_usuario_p);
end if;
 
if (ie_tipo_item_p = 'P') then 
	delete	from pls_solic_lib_mat_med 
	where 	nr_seq_item_proc = nr_seq_item_p;
	 
	select	max(nr_seq_lote_anexo_guia) 
	into STRICT	nr_seq_anexo_w 
	from	pls_lote_anexo_proc_aut 
	where	nr_seq_req_proc = nr_seq_item_p;
	 
	if (nr_seq_anexo_w IS NOT NULL AND nr_seq_anexo_w::text <> '') then 
		delete from pls_lote_anexo_proc_aut 
		where	nr_seq_req_proc = nr_seq_item_p;
	end if;
	 
	delete	from pls_requisicao_proc 
	where 	nr_seq_requisicao = nr_seq_requisicao_p 
	and  	nr_sequencia 	 = nr_seq_item_p;
 
else 
	delete	from pls_solic_lib_mat_med 
	where 	nr_seq_item_mat = nr_seq_item_p;
	 
	select	max(nr_seq_lote_anexo_guia) 
	into STRICT	nr_seq_anexo_w 
	from	pls_lote_anexo_mat_aut 
	where	nr_seq_req_mat = nr_seq_item_p;
	 
	if (nr_seq_anexo_w IS NOT NULL AND nr_seq_anexo_w::text <> '') then 
		delete from pls_lote_anexo_mat_aut 
		where	nr_seq_req_mat = nr_seq_item_p;
	end if;
	 
	delete	from pls_requisicao_mat 
	where 	nr_seq_requisicao = nr_seq_requisicao_p 
	and  	nr_sequencia 	 = nr_seq_item_p;
end if;
 
if (nr_seq_anexo_w IS NOT NULL AND nr_seq_anexo_w::text <> '') then 
	select	sum(qt) 
	into STRICT	qt_registro_w 
	from (SELECT	count(1) qt 
			 from	pls_lote_anexo_proc_aut 
			 where	nr_seq_lote_anexo_guia = nr_seq_anexo_w 
			 
union all
 
			 SELECT	count(1) qt 
			 from	pls_lote_anexo_mat_aut 
			 where	nr_seq_lote_anexo_guia = nr_seq_anexo_w) alias4;
 
	if (qt_registro_w = 0) then 
		delete from pls_lote_anexo_guias_aut 
		where nr_sequencia = nr_seq_anexo_w;
	end if;
end if;
 
commit;
 
if (coalesce(ie_consistir_req_p,'S') = 'S') then 
	select 	sum(valor) 
	into STRICT	qt_registro_w 
	from (SELECT	count(1) valor 
			 from	pls_requisicao_proc 
			 where	nr_seq_requisicao = nr_seq_requisicao_p 
			 
union
 
			 SELECT count(1) valor 
			 from	pls_requisicao_mat 
			 where	nr_seq_requisicao = nr_seq_requisicao_p) alias5;
		 
	if (qt_registro_w > 0) then 
		CALL pls_consistir_requisicao(nr_seq_requisicao_p, cd_estabelecimento_p, nm_usuario_p);
	end if;
end if;
 
end;
$body$
