SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_itens_analise ( nr_seq_analise_p bigint, ds_sequencia_procs_p text, ds_sequencia_mats_p text, ds_sequencia_partic_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ds_sequencia_procs_w		varchar(4000);
ds_sequencia_mats_w		varchar(4000);
ds_sequencia_partic_w		varchar(4000);
nr_seq_proc_w			bigint;
nr_seq_mat_w			bigint;
nr_seq_proc_partic_w		bigint;
nr_seq_partic_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_proc_participante
	where	nr_seq_conta_proc	= nr_seq_proc_w
	order by 1;

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN
ds_sequencia_procs_w	:= ds_sequencia_procs_p;
ds_sequencia_mats_w	:= ds_sequencia_mats_p;
ds_sequencia_partic_w	:= ds_sequencia_partic_p;

while(position(','  ds_sequencia_procs_w) <> 0) loop
	begin
	nr_seq_proc_w		:= substr(ds_sequencia_procs_w,1,position(',' in ds_sequencia_procs_w) - 1);
	ds_sequencia_procs_w	:= substr(ds_sequencia_procs_w,position(',' in ds_sequencia_procs_w) + 1,255);

	delete	from pls_analise_glo_ocor_grupo	a
	where	a.nr_seq_ocor_benef in (SELECT	x.nr_sequencia
					from	pls_ocorrencia_benef	x
					where	coalesce(x.nr_seq_guia_plano::text, '') = ''
					and	coalesce(x.nr_seq_requisicao::text, '') = ''
					and	x.nr_seq_proc        = nr_seq_proc_w);

	delete	from pls_ocorrencia_benef
	where	coalesce(nr_seq_guia_plano::text, '') = ''
	and	coalesce(nr_seq_requisicao::text, '') = ''
	and	nr_seq_proc        = nr_seq_proc_w;

	delete	from pls_analise_glo_ocor_grupo	a
	where	a.nr_seq_conta_glosa in (SELECT	x.nr_sequencia
					from	pls_conta_glosa	x
					where	x.nr_seq_conta_proc	= nr_seq_proc_w);

	delete	from pls_conta_glosa
	where	nr_seq_conta_proc	= nr_seq_proc_w;

	delete	from pls_conta_log
	where	nr_seq_conta_proc	= nr_seq_proc_w;

	open C01;
	loop
	fetch C01 into
		nr_seq_proc_partic_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		delete	from pls_ocorrencia_benef
		where	coalesce(nr_seq_guia_plano::text, '') = ''
		and	coalesce(nr_seq_requisicao::text, '') = ''
		and	coalesce(nr_seq_conta_pos_estab::text, '') = ''
		and	nr_seq_proc_partic      = nr_seq_proc_partic_w;

		delete	from pls_conta_glosa
		where	nr_seq_proc_partic	= nr_seq_proc_partic_w;

		delete	from pls_analise_fluxo_item
		where	nr_seq_proc_partic	= nr_seq_proc_partic_w;

		delete	from pls_conta_pos_estab_partic
		where	nr_seq_proc_partic	= nr_seq_proc_partic_w;

		delete	from pls_proc_participante
		where	nr_sequencia	= nr_seq_proc_partic_w;
		end;
	end loop;
	close C01;

	delete	from pls_analise_fluxo_item
	where	nr_seq_conta_proc	= nr_seq_proc_w;

	delete	from pls_analise_conta_item
	where	nr_seq_proc_partic	= nr_seq_partic_w;

	delete	from pls_proc_participante
	where	nr_sequencia	= nr_seq_partic_w;

	delete	from pls_analise_conta_item
	where	nr_seq_conta_proc	= nr_seq_proc_w;

	delete	from w_pls_resumo_conta
	where	nr_seq_conta_proc	= nr_seq_proc_w;

	delete	from pls_hist_analise_conta
	where	nr_seq_conta_proc	= nr_seq_proc_w;

	update	pls_conta_proc
	set	nr_seq_proc_princ	 = NULL
	where	nr_seq_proc_princ	= nr_seq_proc_w;

	delete	from pls_conta_proc
	where	nr_sequencia	= nr_seq_proc_w;
	end;
end loop;

while(position(',' in ds_sequencia_mats_w) <> 0) loop
	begin
	nr_seq_mat_w		:= substr(ds_sequencia_mats_w,1,position(',' in ds_sequencia_mats_w) - 1);
	ds_sequencia_mats_w	:= substr(ds_sequencia_mats_w,position(',' in ds_sequencia_mats_w)  + 1,255);

	delete	from pls_analise_glo_ocor_grupo	a
	where	a.nr_seq_ocor_benef in (SELECT	x.nr_sequencia
					from	pls_ocorrencia_benef	x
					where	coalesce(x.nr_seq_guia_plano::text, '') = ''
					and	coalesce(x.nr_seq_requisicao::text, '') = ''
					and	x.nr_seq_mat           = nr_seq_mat_w);

	delete	from pls_ocorrencia_benef
	where	coalesce(nr_seq_guia_plano::text, '') = ''
	and	coalesce(nr_seq_requisicao::text, '') = ''
	and	nr_seq_mat           = nr_seq_mat_w;

	delete	from pls_analise_glo_ocor_grupo	a
	where	a.nr_seq_conta_glosa in (SELECT	x.nr_sequencia
					from	pls_conta_glosa	x
					where	x.nr_seq_conta_mat      = nr_seq_mat_w);

	delete	from pls_conta_glosa
	where	nr_seq_conta_mat      = nr_seq_mat_w;

	delete	from pls_analise_fluxo_item
	where	nr_seq_conta_mat	= nr_seq_mat_w;

	delete	from pls_analise_conta_item
	where	nr_seq_conta_mat	= nr_seq_mat_w;

	delete	from w_pls_resumo_conta
	where	nr_seq_conta_mat	= nr_seq_mat_w;

	delete	from pls_hist_analise_conta
	where	nr_seq_conta_mat	= nr_seq_mat_w;

	delete	from pls_conta_log
	where	nr_seq_conta_mat	= nr_seq_mat_w;

	update	pls_conta_mat
	set	nr_seq_mat_princ	 = NULL
	where	nr_seq_mat_princ	= nr_seq_mat_w;

	delete	from pls_conta_mat
	where	nr_sequencia	= nr_seq_mat_w;
	end;
end loop;

while(position(',' in ds_sequencia_partic_w) <> 0) loop
	begin
	nr_seq_partic_w		:= substr(ds_sequencia_partic_w,1,position(',' in ds_sequencia_partic_w)- 1);
	ds_sequencia_partic_w	:= substr(ds_sequencia_partic_w,position(',' in ds_sequencia_partic_w)  + 1,255);

	delete	from pls_ocorrencia_benef
	where	coalesce(nr_seq_guia_plano::text, '') = ''
	and	coalesce(nr_seq_requisicao::text, '') = ''
	and	nr_seq_proc_partic   = nr_seq_partic_w;

	delete	from pls_conta_glosa
	where	nr_seq_proc_partic	= nr_seq_partic_w;

	delete	from pls_analise_fluxo_item
	where	nr_seq_proc_partic	= nr_seq_partic_w;

	delete	from pls_conta_pos_estab_partic
	where	nr_seq_proc_partic	= nr_seq_partic_w;

	delete	from pls_analise_conta_item
	where	nr_seq_proc_partic	= nr_seq_partic_w;

	delete	from pls_proc_participante
	where	nr_sequencia	= nr_seq_partic_w;
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
