CREATE OR REPLACE PROCEDURE pls_excluir_itens_guia ( nr_seq_guia_p bigint, ie_exc_itens_neg_audit_p text, nr_seq_motivo_exclusao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_status_mat_w			varchar(2);
ie_status_proc_w		varchar(2);
nr_seq_guia_proc_w		bigint;
nr_seq_guia_mat_w		bigint;
nr_seq_motivo_lib_w		bigint;

c01 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_status 
	from	pls_guia_plano_proc 
	where	nr_seq_guia	= nr_seq_guia_p 
	and 	coalesce(nr_seq_motivo_exc::text, '') = '';

c02 CURSOR FOR 
	SELECT	nr_sequencia, 
		ie_status 
	from	pls_guia_plano_mat 
	where	nr_seq_guia	= nr_seq_guia_p 
	and 	coalesce(nr_seq_motivo_exc::text, '') = '';


BEGIN 
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') and (coalesce(nr_seq_guia_p,0) <> 0) then 
	open c01;
	loop 
	fetch c01 into	 
		nr_seq_guia_proc_w, 
		ie_status_proc_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		if	((ie_status_proc_w = 'N') or 
			(ie_status_proc_w = 'M' AND ie_exc_itens_neg_audit_p = 'S')) then 
			update pls_guia_plano_proc 
			set	nr_seq_motivo_exc = nr_seq_motivo_exclusao_p 
			where	nr_sequencia	= nr_seq_guia_proc_w;
		end if;
		end;
	end loop;
	close c01;
 
	open c02;
	loop 
	fetch c02 into	 
		nr_seq_guia_mat_w, 
		ie_status_mat_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
		if	((ie_status_mat_w = 'N') or 
			(ie_status_mat_w = 'M' AND ie_exc_itens_neg_audit_p = 'S')) then 
			update pls_guia_plano_mat 
			set	nr_seq_motivo_exc = nr_seq_motivo_exclusao_p 
			where	nr_sequencia	= nr_seq_guia_mat_w;
		end if;
		end;
	end loop;
	close c02;
 
	delete	from pls_guia_glosa 
	where	nr_seq_guia	= nr_seq_guia_p;
 
	delete	from pls_guia_glosa 
	where	coalesce(nr_seq_guia::text, '') = '' 
	and	coalesce(nr_seq_guia_proc::text, '') = '' 
	and	coalesce(nr_seq_guia_mat::text, '') = '';
 
	delete	from pls_guia_motivo_lib 
	where	coalesce(nr_seq_guia::text, '') = '' 
	and	coalesce(nr_seq_guia_proc::text, '') = '' 
	and	coalesce(nr_seq_guia_mat::text, '') = '';
 
	CALL pls_consistir_guia(nr_seq_guia_p, cd_estabelecimento_p, nm_usuario_p);
end if;
 
commit;
 
end;
$body$
