CREATE OR REPLACE PROCEDURE pls_excluir_itens_requisicao ( nr_seq_req_proc_p bigint, nr_seq_req_mat_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_req_proc_p IS NOT NULL AND nr_seq_req_proc_p::text <> '') and (coalesce(nr_seq_req_proc_p,0)	<> 0)then

	delete  from pls_ocorrencia_benef
	where	nr_seq_proc = nr_seq_req_proc_p
	and	(nr_seq_requisicao IS NOT NULL AND nr_seq_requisicao::text <> '');

	delete	from pls_requisicao_glosa
	where	nr_seq_req_proc		= nr_seq_req_proc_p;

elsif (nr_seq_req_mat_p IS NOT NULL AND nr_seq_req_mat_p::text <> '') and (coalesce(nr_seq_req_mat_p,0)	<> 0)then

	delete  from pls_ocorrencia_benef
	where	nr_seq_mat = nr_seq_req_mat_p
	and	(nr_seq_requisicao IS NOT NULL AND nr_seq_requisicao::text <> '');

	delete	from pls_requisicao_glosa
	where	nr_seq_req_mat		= nr_seq_req_mat_p;

end if;

commit;

end;
$body$
