CREATE OR REPLACE PROCEDURE pls_excluir_local_atendimento (nr_seq_local_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	local_atend_med_maquina
	where	nr_seq_local_atend  = nr_seq_local_p;

nr_seq_maquina_w	 bigint;


BEGIN

if (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		nr_seq_maquina_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

		update local_atend_med_maq_finger
		set	dt_fim_licenca = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_seq_maquina = nr_seq_maquina_w;

	end loop;
	close C01;

	update  local_atend_med_maquina
	set     ie_situacao = 'I',
	        nm_usuario = nm_usuario_p,
	        dt_atualizacao = clock_timestamp()
	where   nr_seq_local_atend  = nr_seq_local_p;

	update  local_atend_med_prest
	set     nm_usuario = nm_usuario_p,
	        dt_atualizacao = clock_timestamp(),
	        dt_fim_vigencia = clock_timestamp()
	where   nr_seq_local_atend = nr_seq_local_p;

	update  local_atendimento_medico
	set	ie_situacao = 'I',
	        nm_usuario = nm_usuario_p,
	        dt_atualizacao = clock_timestamp()
	where   nr_sequencia = nr_seq_local_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
