CREATE OR REPLACE PROCEDURE pls_excluir_lote_carteira ( nr_seq_lote_p pls_carteira_emissao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_registros_w	bigint;


BEGIN

select	count(1)
into STRICT	qt_registros_w
from	pls_carteira_emissao
where	nr_seq_lote = nr_seq_lote_p;

if (qt_registros_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1179718);
end if;

delete from pls_lote_cart_historico
where 	nr_seq_lote_carteira = nr_seq_lote_p;

delete from pls_lote_carteira
where	nr_sequencia = nr_seq_lote_p;

commit;

end;
$body$
