CREATE OR REPLACE PROCEDURE pls_excluir_lote_conferencia ( nr_seq_lote_p pls_lot_monit_tiss_confer.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
qt_reg_w		integer;

C01 CURSOR(nr_seq_lote_pc	pls_lot_monit_tiss_confer.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_monit_tiss_confer
	where	nr_seq_lote = nr_seq_lote_pc;


BEGIN

Open C01(nr_seq_lote_p);
loop
	tb_nr_sequencia_w.delete;

	fetch C01 bulk collect into tb_nr_sequencia_w
	limit pls_util_pck.qt_registro_transacao_w;

	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		delete	FROM pls_monit_tiss_confer
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close C01;

select	count(1)
into STRICT	qt_reg_w
from	pls_monit_tiss_confer
where	nr_seq_lote = nr_seq_lote_p;

if (qt_reg_w = 0) then
	delete	FROM pls_lot_monit_tiss_confer
	where	nr_sequencia = nr_seq_lote_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
