CREATE OR REPLACE PROCEDURE pls_excluir_lote_contestacao ( nr_seq_lote_contest_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Excluir o lote de discussão
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/BEGIN
delete	FROM pls_contestacao_anexo
where	nr_seq_lote_contest = nr_seq_lote_contest_p;

delete	FROM pls_contest_item_glosa
where	nr_seq_contest_mat in (SELECT	nr_sequencia
				from	pls_contestacao_mat
				where	nr_seq_contestacao in (select	nr_sequencia
								from	pls_contestacao
								where	nr_seq_lote = nr_seq_lote_contest_p));

delete	FROM pls_contestacao_mat
where	nr_seq_contestacao in (SELECT	nr_sequencia
				from	pls_contestacao
				where	nr_seq_lote = nr_seq_lote_contest_p);

delete	FROM pls_contest_item_glosa
where	nr_seq_contest_proc in (SELECT	nr_sequencia
				from	pls_contestacao_proc
				where	nr_seq_contestacao in (select	nr_sequencia
								from	pls_contestacao
								where	nr_seq_lote = nr_seq_lote_contest_p));

delete	FROM pls_contestacao_proc
where	nr_seq_contestacao in (SELECT	nr_sequencia
				from	pls_contestacao
				where	nr_seq_lote = nr_seq_lote_contest_p);

delete	FROM pls_contestacao
where	nr_seq_lote = nr_seq_lote_contest_p;

--delete	pls_lote_contestacao
--where	nr_sequencia = nr_seq_lote_contest_p;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
