CREATE OR REPLACE PROCEDURE pls_excluir_lote_imp_web ( nr_seq_lote_p pls_protocolo_conta.nr_seq_lote_conta%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Excluir o lote de importacaoo de XML
----------------------------------------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionario [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatorios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------

Pontos de atencao:  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/				
				
nr_seq_protocolo_w		bigint;

c01 CURSOR(nr_seq_lote_pc	pls_protocolo_conta.nr_seq_lote_conta%type) FOR
	SELECT	nr_sequencia nr_seq_protocolo
	from	pls_protocolo_conta
	where   nr_seq_lote_conta = nr_seq_lote_pc
	order by nr_sequencia desc;
	
BEGIN

for r_c01_w in C01(nr_seq_lote_p) loop
	CALL pls_excluir_protocolo_imp(r_c01_w.nr_seq_protocolo, nm_usuario_p, cd_estabelecimento_p);
end loop;


delete from  pls_xml_lote
where  nr_seq_lote = nr_seq_lote_p
and    ie_tipo_processo = 1;

delete	from  pls_lote_protocolo_conta
where	nr_sequencia = nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
