CREATE OR REPLACE PROCEDURE pls_excluir_lote_inclusao_web ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Excluir lote de incllusão de beneficiários, caso não haja beneficiário incluso.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_beneficiario_lote_w	bigint;
nr_seq_inclusao_benef_w 	pls_inclusao_beneficiario.nr_sequencia%type;


C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_inclusao_beneficiario a
	where	a.nr_seq_lote_inclusao = nr_seq_lote_p;


BEGIN

select	count(*)
into STRICT 	qt_beneficiario_lote_w
from	pls_inclusao_beneficiario a
where	a.nr_seq_lote_inclusao = nr_seq_lote_p;

if (qt_beneficiario_lote_w > 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_inclusao_benef_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		delete 	from 	pls_homonimo_pessoa_fisica
		where 	nr_seq_inclusao_benef = nr_seq_inclusao_benef_w;

		end;
	end loop;
	close C01;

	delete 	from 	pls_inclusao_beneficiario
	where	nr_seq_lote_inclusao = nr_seq_lote_p;
end if;

delete	from	pls_lote_inclusao_benef
where	nr_sequencia	= nr_seq_lote_p;

commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
