SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_mens_rec ( nr_seq_mens_cancel_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: excluir a mensalidade
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_mensalidade_lote_w	bigint;
nr_seq_lote_w		bigint;

BEGIN

-- verifica quantas mensalidades existem no lote e retorna o código do lote
select	b.nr_seq_lote,
	count(b.nr_sequencia)
into STRICT	nr_seq_lote_w,
	qt_mensalidade_lote_w
from	pls_cancel_rec_mensalidade a,
	pls_cancel_rec_mensalidade b
where	a.nr_sequencia	= nr_seq_mens_cancel_p
and	b.nr_seq_lote	= a.nr_seq_lote
group by b.nr_seq_lote;

delete
from	pls_cancel_rec_mens_incons
where	nr_seq_mens_cancel = nr_seq_mens_cancel_p;

delete
from	pls_cancel_rec_mensalidade
where	nr_sequencia = nr_seq_mens_cancel_p;

-- se acaso não existirem mais mensalidades para o lote, coloca o mesmo como provisório
if (qt_mensalidade_lote_w = 1) then
	CALL pls_atualizar_stat_lote_canc(	nr_seq_lote_w,
					'P',
					'M',
					nm_usuario_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_mens_rec ( nr_seq_mens_cancel_p bigint, nm_usuario_p text) FROM PUBLIC;

