CREATE OR REPLACE PROCEDURE pls_excluir_nota_deb_a560 ( nr_seq_nota_debito_p ptu_nota_debito.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


qt_registro_w		integer;


BEGIN
select	count(qt)
into STRICT	qt_registro_w
from (SELECT	1 qt
	from	titulo_pagar		tp,
		ptu_nota_deb_conclusao	dc
	where	dc.nr_sequencia		= tp.nr_seq_nota_deb_conclusao
	and	dc.nr_seq_nota_debito	= nr_seq_nota_debito_p
	
union all

	SELECT	1 qt
	from	titulo_receber		tr,
		ptu_nota_deb_conclusao	dc
	where	dc.nr_sequencia		= tr.nr_seq_nota_deb_conclusao
	and	dc.nr_seq_nota_debito	= nr_seq_nota_debito_p) alias1;


if (qt_registro_w > 0) then
	-- Nao e possivel excluir um arquivo A560 com titulo vinculado.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1032536);
end if;

delete	FROM ptu_nota_deb_fat_ndr
where	nr_seq_nota_debito	= nr_seq_nota_debito_p;

delete	FROM ptu_nota_deb_dados
where	nr_seq_nota_debito	= nr_seq_nota_debito_p;

delete	FROM ptu_nota_deb_credor_deved
where	nr_seq_nota_debito	= nr_seq_nota_debito_p;

delete	FROM ptu_nota_deb_bol_inst
where	nr_seq_nota_deb_bol	in (	SELECT	nr_sequencia
					from	ptu_nota_deb_bol
					where	nr_seq_nota_debito	= nr_seq_nota_debito_p);

delete	FROM ptu_nota_deb_bol_obs
where	nr_seq_nota_deb_bol	in (	SELECT	nr_sequencia
					from	ptu_nota_deb_bol
					where	nr_seq_nota_debito	= nr_seq_nota_debito_p);

delete	FROM ptu_nota_deb_bol_ld
where	nr_seq_nota_deb_bol	in (	SELECT	nr_sequencia
					from	ptu_nota_deb_bol
					where	nr_seq_nota_debito	= nr_seq_nota_debito_p);

delete	FROM ptu_nota_deb_bol
where	nr_seq_nota_debito	= nr_seq_nota_debito_p;

delete	FROM ptu_nota_deb_conclusao
where	nr_seq_nota_debito	= nr_seq_nota_debito_p;

update 	pls_recebimento_ptu pr
set	pr.nr_seq_nota_debito  = NULL,
	pr.ie_status = 'P' -- Pendente
where	pr.nr_seq_nota_debito = nr_seq_nota_debito_p;

delete	FROM ptu_nota_debito
where	nr_sequencia	= nr_seq_nota_debito_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
