CREATE OR REPLACE PROCEDURE pls_excluir_parcela_estrut ( nr_seq_escrituracao_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_escrituracao_p IS NOT NULL AND nr_seq_escrituracao_p::text <> '') then
	delete 	from pls_escrit_quota_parcela
	where	nr_seq_escrituracao = nr_seq_escrituracao_p;

	update	pls_escrituracao_quota
	set	dt_geracao_parcelas 	 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_escrituracao_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
