CREATE OR REPLACE PROCEDURE pls_excluir_pendencias_guia ( nr_seq_guia_p bigint, nr_seq_guia_proc_p bigint, nr_seq_guia_mat_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_reg_proc_w			bigint;
qt_reg_mat_w			bigint;
ie_status_w			varchar(255);


BEGIN

if (nr_seq_guia_proc_p IS NOT NULL AND nr_seq_guia_proc_p::text <> '') then
	begin
		select	ie_status
		into STRICT	ie_status_w
		from	pls_guia_plano_proc
		where	nr_sequencia	= nr_seq_guia_proc_p;
	exception
	when others then
		ie_status_w	:= null;
	end;

	if (ie_status_w	<> 'U') then

		CALL wheb_mensagem_pck.exibir_mensagem_abort(237878);
	end if;

	select	count(1)
	into STRICT	qt_reg_proc_w
	from	pls_guia_coparticipacao
	where	nr_seq_guia		= nr_seq_guia_p
	and	nr_seq_guia_proc	= nr_seq_guia_proc_p;

	if (qt_reg_proc_w	> 0) then
		delete	from pls_guia_coparticipacao
		where	nr_seq_guia		= nr_seq_guia_p
		and	nr_seq_guia_proc	= nr_seq_guia_proc_p;
	end if;
elsif (nr_seq_guia_mat_p IS NOT NULL AND nr_seq_guia_mat_p::text <> '') then
	begin
		select	ie_status
		into STRICT	ie_status_w
		from	pls_guia_plano_mat
		where	nr_sequencia	= nr_seq_guia_mat_p;
	exception
	when others then
		ie_status_w	:= null;
	end;

	if (ie_status_w	<> 'U') then

		CALL wheb_mensagem_pck.exibir_mensagem_abort(237878);
	end if;

	select	count(1)
	into STRICT	qt_reg_mat_w
	from	pls_guia_coparticipacao
	where	nr_seq_guia		= nr_seq_guia_p
	and	nr_seq_guia_mat		= nr_seq_guia_mat_p;

	if (qt_reg_mat_w	> 0) then
		delete	from pls_guia_coparticipacao
		where	nr_seq_guia		= nr_seq_guia_p
		and	nr_seq_guia_mat		= nr_seq_guia_mat_p;
	end if;
end if;

commit;

end;
$body$
