SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_proposta_comercial ( nr_proposta_p bigint) AS $body$
DECLARE


nr_seq_cliente_w	bigint;
nr_seq_canal_w		bigint;
nr_seq_historico_w	bigint;
ie_origem_cliente_w	varchar(2);


BEGIN

select  coalesce(max(nr_seq_cliente), 0) cliente
into STRICT	nr_seq_cliente_w
from    pls_proposta_adesao
where	nr_sequencia = nr_proposta_p;


if (nr_seq_cliente_w > 0)then
	select	ie_origem_cliente
	into STRICT	ie_origem_cliente_w
	from	pls_comercial_cliente
	where	nr_sequencia = nr_seq_cliente_w;

	if ((ie_origem_cliente_w IS NOT NULL AND ie_origem_cliente_w::text <> '') and ie_origem_cliente_w = 'PW')then
		select	coalesce(max(nr_sequencia), 0)canal
		into STRICT	nr_seq_canal_w
		from	pls_solicitacao_vendedor
		where	nr_seq_cliente = nr_seq_cliente_w;

		select 	coalesce(max(nr_sequencia),0) historico
		into STRICT	nr_seq_historico_w
		from	pls_comercial_historico
		where 	nr_seq_cliente = nr_seq_cliente_w;


		if (nr_seq_canal_w > 0)then
			delete 	from pls_solicitacao_vendedor
			where	nr_sequencia = nr_seq_canal_w
			and	nr_seq_cliente = nr_seq_cliente_w;
		end if;

		if (nr_seq_historico_w > 0)then
			delete	from pls_comercial_historico
			where	nr_sequencia = nr_seq_historico_w
			and	nr_seq_cliente = nr_seq_cliente_w;
		end if;

		update 	pls_proposta_adesao
		set 	nr_seq_cliente  = NULL
		where 	nr_sequencia = nr_proposta_p;

		delete	from pls_comercial_cliente
		where	nr_sequencia = nr_seq_cliente_w;
	end if;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
