SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_prop_adesao_web ( nr_seq_lote_p bigint) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Excluir a proposta de adesão do beneficiário
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/qt_lotes_w		bigint;
nr_seq_beneficiario_w	bigint;


C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_inclusao_beneficiario a
	where	a.nr_seq_lote_inclusao = nr_seq_lote_p;


BEGIN


select	count(*)
into STRICT	qt_lotes_w
from	pls_lote_inclusao_benef
where   nr_sequencia = nr_seq_lote_p
and 	ie_situacao <> 'L'
and 	ie_situacao <> 'IP';

if (qt_lotes_w = 1) then

	open C01;
	loop
	fetch C01 into
		nr_seq_beneficiario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		delete 	from 	pls_homonimo_pessoa_fisica
		where   nr_seq_inclusao_benef = nr_seq_beneficiario_w;

		delete 	from 	pls_inconsist_incl_benef
		where	nr_seq_solic_inclusao = nr_seq_beneficiario_w;

		end;
	end loop;
	close C01;

	delete 	from    pls_inclusao_anexo
	where 	nr_seq_lote_inclusao = nr_seq_lote_p;

	delete 	from    pls_inclusao_beneficiario
	where 	nr_seq_lote_inclusao = nr_seq_lote_p;


	delete  from 	pls_lote_inclusao_benef
	where 	nr_sequencia = nr_seq_lote_p;

	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_prop_adesao_web ( nr_seq_lote_p bigint) FROM PUBLIC;

