CREATE OR REPLACE PROCEDURE pls_excluir_reajuste_tab_selec ( nr_seq_reajuste_tab_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_reajuste_status_w	varchar(10);


BEGIN

select	max(a.ie_status)
into STRICT	ie_reajuste_status_w
from	pls_reajuste_tabela	b,
	pls_reajuste		a
where	b.nr_seq_reajuste	=  a.nr_sequencia
and	b.nr_sequencia		=  nr_seq_reajuste_tab_p;

if (ie_reajuste_status_w = '1') then
	delete	FROM pls_reajuste_tabela
	where	nr_sequencia	= nr_seq_reajuste_tab_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
