CREATE OR REPLACE PROCEDURE pls_excluir_recurso_web (nr_seq_conta_rec_p bigint, nr_seq_conta_proc_rec_p bigint, nr_seq_conta_mat_rec_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

if (nr_seq_conta_proc_rec_p IS NOT NULL AND nr_seq_conta_proc_rec_p::text <> '') then
	delete	FROM pls_rec_glosa_proc
	where	nr_sequencia = nr_seq_conta_proc_rec_p;
elsif (nr_seq_conta_mat_rec_p IS NOT NULL AND nr_seq_conta_mat_rec_p::text <> '') then
	delete	FROM pls_rec_glosa_mat
	where	nr_sequencia = nr_seq_conta_mat_rec_p;
elsif (nr_seq_conta_rec_p IS NOT NULL AND nr_seq_conta_rec_p::text <> '') then
	delete 	FROM pls_rec_glosa_anexo
	where 	nr_seq_conta_rec = nr_seq_conta_rec_p;

	delete	FROM pls_rec_glosa_conta
	where	nr_sequencia = nr_seq_conta_rec_p;
end if;


if ((nr_seq_conta_proc_rec_p IS NOT NULL AND nr_seq_conta_proc_rec_p::text <> '') or (nr_seq_conta_mat_rec_p IS NOT NULL AND nr_seq_conta_mat_rec_p::text <> '')) then
	select	sum(qt)
	into STRICT	qt_registros_w
	from (SELECT	count(1) qt
			 from	pls_rec_glosa_mat
			 where	nr_seq_conta_rec = nr_seq_conta_rec_p
			
union all

			 SELECT	count(1) qt
			 from	pls_rec_glosa_proc
			 where	nr_seq_conta_rec = nr_seq_conta_rec_p) alias6;

	if (qt_registros_w = 0) then

		delete 	FROM pls_rec_glosa_anexo
		where 	nr_seq_conta_rec = nr_seq_conta_rec_p;

		delete	FROM pls_rec_glosa_conta
		where	nr_sequencia = nr_seq_conta_rec_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
