SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_reembolso ( nr_seq_reembolso_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_seq_reemb_proc_w		bigint;
nr_seq_reemb_mat_w		bigint;
nr_titulo_w			bigint;
nr_lote_contabil_w		bigint;
ie_mes_fechado_w		varchar(1);

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_reembolso_proc 
	where	nr_seq_reembolso = nr_seq_reembolso_p;
	
C02 CURSOR FOR 
	SELECT	nr_titulo 
	from	titulo_pagar 
	where	nr_seq_reembolso = nr_seq_reembolso_p;


BEGIN 
 
select	coalesce(max(nr_lote_contabil),0) 
into STRICT	nr_lote_contabil_w 
from	pls_reembolso 
where	nr_sequencia	= nr_seq_reembolso_p;
 
select	pls_obter_se_mes_fechado(dt_referencia,'T', cd_estabelecimento_p) 
into STRICT	ie_mes_fechado_w 
from	pls_reembolso 
where	nr_sequencia	= nr_seq_reembolso_p;
 
if (ie_mes_fechado_w	= 'S') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267002, null); /* O mês de competência ou contabilidade do mês está fechado. Verifique! */
end if;
 
if (nr_lote_contabil_w > 0) then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 267003, null); /* Não é possível excluir o reembolso pois o mesmo já foi contabilizado. */
end if;
 
delete	from pls_reembolso_hist 
where	nr_seq_reembolso	= nr_seq_reembolso_p;
 
delete	from pls_reemb_coparticipacao 
where	nr_seq_reembolso	= nr_seq_reembolso_p;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_reemb_proc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	delete	from pls_reembolso_proc_partic 
	where	nr_seq_proc_reembolso = nr_seq_reemb_proc_w;
	 
	delete	from pls_reembolso_glosa 
	where	nr_seq_reemb_proc = nr_seq_reemb_proc_w;
	end;
end loop;
close C01;
 
open C02;
loop 
fetch C02 into	 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	CALL Cancelar_Titulo_Pagar(nr_titulo_w,nm_usuario_p, clock_timestamp());
	end;
end loop;
close C02;
 
delete	from pls_reembolso_proc 
where	nr_seq_reembolso	= nr_seq_reembolso_p;
 
delete	from pls_reembolso_mat 
where	nr_seq_reembolso	= nr_seq_reembolso_p;
 
delete	from pls_reembolso 
where	nr_sequencia = nr_seq_reembolso_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_reembolso ( nr_seq_reembolso_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

