CREATE OR REPLACE PROCEDURE pls_excluir_regras_apropriacao ( nr_seq_regra_aprop_p pls_regra_copartic.nr_sequencia%type ) AS $body$
BEGIN

if (nr_seq_regra_aprop_p IS NOT NULL AND nr_seq_regra_aprop_p::text <> '') then

	delete 	from pls_regra_copartic_aprop
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete 	from pls_regra_copartic_benef
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete 	from pls_regra_copartic_conta
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete 	from pls_regra_copartic_util
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete 	from pls_regra_copartic_prest
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete 	from pls_regra_copartic_guia
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete 	from pls_regra_copartic_interna
	where	nr_seq_regra = nr_seq_regra_aprop_p;

	delete	from pls_regra_copartic
	where	nr_sequencia = nr_seq_regra_aprop_p;

end if;

commit;

end;
$body$
