CREATE OR REPLACE PROCEDURE pls_excluir_sca_simulacao ( nr_seq_simulacao_p bigint, nr_seq_plano_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_vinculo_sca_w	bigint;
nr_seq_simul_benef_w	bigint;

c01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_simulacao_preco	c, 
		pls_simulpreco_individual	b, 
		pls_sca_vinculo		a 
	where	b.nr_seq_simulacao		= c.nr_sequencia 
	and	a.nr_seq_segurado_simul		= b.nr_sequencia 
	and	c.nr_sequencia			= nr_seq_simulacao_p 
	and	a.nr_seq_plano			= nr_seq_plano_p;
	
C02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_simulpreco_individual 
	where	nr_seq_simulacao	= nr_seq_simulacao_p;


BEGIN 
 
/*Retirar SCA's do resumo da simulação*/
 
open c01;
loop 
fetch c01 into 
	nr_seq_vinculo_sca_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	CALL pls_gerar_resumo_simulacao(nr_seq_simulacao_p,nr_seq_vinculo_sca_w,'D','SC','I',cd_estabelecimento_p,nm_usuario_p);
 
	delete	FROM pls_sca_vinculo 
	where	nr_sequencia	= nr_seq_vinculo_sca_w;
 
	end;
end loop;
close c01;
/*Recalcular a simulação*/
 
CALL pls_recalcular_simulacao(nr_seq_simulacao_p,nm_usuario_p);
 
/*Atualizar os dados do resumo*/
 
open C02;
loop 
fetch C02 into	 
	nr_seq_simul_benef_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	 
	CALL pls_gerar_resumo_simulacao(nr_seq_simulacao_p,nr_seq_simul_benef_w,'E','BS','I',cd_estabelecimento_p,nm_usuario_p);
	 
	end;
end loop;
close C02;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
