CREATE OR REPLACE PROCEDURE pls_excluir_simulacao_preco ( nr_seq_simulacao_p pls_simulacao_preco.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_simulpreco_individual
	where	nr_seq_simulacao	= nr_seq_simulacao_p;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_simulacao_perfil
	where	nr_seq_simulacao	= nr_seq_simulacao_p;

C03 CURSOR(nr_seq_simul_perfil_pc	pls_simulacao_perfil.nr_sequencia%type) FOR
	SELECT	nr_sequencia
	from	pls_tabela_preco
	where	nr_seq_simul_perfil	= nr_seq_simul_perfil_pc;
BEGIN

for r_c01_w in C01 loop
	begin
	delete	from	pls_sca_vinculo
	where	nr_seq_segurado_simul	= r_c01_w.nr_sequencia;

	delete	from	pls_bonificacao_vinculo
	where	nr_seq_segurado_simul	= r_c01_w.nr_sequencia;
	end;
end loop;

for r_c02_w in C02 loop
	begin
	delete	from	pls_simulpreco_coletivo
	where	nr_seq_simul_perfil	= r_c02_w.nr_sequencia;

	delete	from	pls_simul_param_item
	where	nr_seq_regra_perfil	= r_c02_w.nr_sequencia;

	delete	from	pls_sca_vinculo
	where	nr_seq_simulacao_perfil	= r_c02_w.nr_sequencia;

	delete	from	pls_bonificacao_vinculo
	where	nr_seq_simulacao_perfil	= r_c02_w.nr_sequencia;

	delete	from	pls_simul_perfil_tabela
	where	nr_seq_simul_perfil	= r_c02_w.nr_sequencia;

	for r_c03_w in C03(r_c02_w.nr_sequencia) loop
		begin
		delete	from	pls_plano_preco
		where	nr_seq_tabela	= r_c03_w.nr_sequencia;

		delete	from	pls_tabela_preco
		where	nr_sequencia	= r_c03_w.nr_sequencia;
		end;
	end loop;
	end;
end loop;

delete	from	pls_simulacao_perfil
where	nr_seq_simulacao	= nr_seq_simulacao_p;

delete	from	pls_simulpreco_individual
where	nr_seq_simulacao	= nr_seq_simulacao_p;

delete	from	pls_simulacao_preco_plano
where	nr_seq_simulacao	= nr_seq_simulacao_p;

delete	from	pls_sca_vinculo
where	nr_seq_simulacao	= nr_seq_simulacao_p;

delete	from	pls_bonificacao_vinculo
where	nr_seq_simulacao	= nr_seq_simulacao_p;

delete	from	pls_simulacao_preco
where	nr_sequencia	= nr_seq_simulacao_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
