CREATE OR REPLACE PROCEDURE pls_excluir_tabela_preco ( nr_seq_tabela_reaj_p pls_reajuste_tabela.nr_sequencia%type, nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, ie_tipo_reajuste_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


qt_tabelas_w		bigint;
qt_copartic_w		bigint;
qt_tx_insc_w		bigint;
qt_via_cart_w		bigint;
qt_pos_estab_w		bigint;
qt_reaj_apropriacao_w	bigint;


BEGIN

delete	from pls_reajuste_preco
where	nr_seq_tabela = nr_seq_tabela_reaj_p;

delete 	from pls_reajuste_tabela
where	nr_sequencia = nr_seq_tabela_reaj_p;

select	count(1)
into STRICT	qt_tabelas_w
from	pls_reajuste_tabela
where	nr_seq_reajuste = nr_seq_reajuste_p;

select	count(1)
into STRICT	qt_copartic_w
from	pls_lote_reaj_copartic
where	nr_seq_reajuste = nr_seq_reajuste_p;

select	count(1)
into STRICT	qt_tx_insc_w
from	pls_lote_reaj_inscricao
where	nr_seq_reajuste = nr_seq_reajuste_p;

select	count(1)
into STRICT	qt_via_cart_w
from	pls_lote_reaj_via_cart
where	nr_seq_reajuste = nr_seq_reajuste_p;

select	count(1)
into STRICT	qt_pos_estab_w
from	pls_lote_reaj_pos_estab
where	nr_seq_reajuste = nr_seq_reajuste_p;

select	count(1)
into STRICT	qt_reaj_apropriacao_w
from	pls_reajuste_apropriacao
where	nr_seq_reajuste = nr_seq_reajuste_p;

if	((qt_tabelas_w = 0) and (qt_copartic_w = 0) and (qt_tx_insc_w = 0) and (qt_via_cart_w = 0) and (qt_pos_estab_w = 0) and (qt_reaj_apropriacao_w = 0)) then
	if (ie_tipo_reajuste_p = 'C') then
		delete from pls_reajuste
		where	nr_sequencia = nr_seq_reajuste_p;
	elsif (ie_tipo_reajuste_p = 'I') then
		pls_reajuste_individual_pck.desfazer_reajuste(nr_seq_reajuste_p, nm_usuario_p, cd_estabelecimento_p);
	end if;
end if;

commit;

end;
$body$
