CREATE OR REPLACE PROCEDURE pls_excluir_titulo_cobran_terc (nr_seq_lote_p bigint, nr_titulo_p bigint, ie_opcao_p text) AS $body$
BEGIN
/* Excluir todos os títulos do lote */

if (upper(ie_opcao_p) = 'T') and (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	delete
	from	pls_lote_cob_terceiro_item
	where	nr_seq_lote	= nr_seq_lote_p;
end if;

/* Excluir os títulos selecionados */

if (upper(ie_opcao_p) = 'U') and (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') and (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then
	delete
	from	pls_lote_cob_terceiro_item
	where	nr_seq_lote	= nr_seq_lote_p
	and	nr_titulo	= nr_titulo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
