CREATE OR REPLACE PROCEDURE pls_exclusao_recurso_glosa ( ie_tipo_p text, nr_seq_ref_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*
IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P

'L'	->	LOTE
'P'	->	PROTOCOLO

IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P ----- IE_TIPO_P
*/
nr_seq_lote_w	PLS_REC_GLOSA_LOTE.nr_sequencia%type;
contador_w	integer;

C01 CURSOR( ie_tipo_w text, nr_seq_w bigint ) FOR
	SELECT	nr_sequencia
	from	pls_rec_glosa_protocolo
	where	(( ie_tipo_w = 'L' and nr_seq_lote = nr_seq_w ) or ( ie_tipo_w = 'P' and nr_sequencia = nr_seq_w ))
	--	CASO HAJA MAIS ALGUM TIPO DE NR_SEQUENCIA DEVE SER TRATADO AQUI
	order by nr_sequencia;

BEGIN

select	max(nr_seq_lote)
into STRICT	nr_seq_lote_w
from	pls_rec_glosa_protocolo
where	(( ie_tipo_p = 'L' and nr_seq_lote = nr_seq_ref_p ) or ( ie_tipo_p = 'P' and nr_sequencia = nr_seq_ref_p ));

if ( ie_tipo_p = 'L' ) then
	nr_seq_lote_w := nr_seq_ref_p;
end if;

for r_c01_w in C01( ie_tipo_p, nr_seq_ref_p ) loop
	delete FROM pls_rec_retorno_glosa where nr_seq_proc_imp in ( SELECT nr_sequencia from pls_rec_glosa_proc_imp where nr_seq_conta_imp in ( select nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) ) );
	delete FROM pls_rec_glosa_glosas where nr_seq_proc_rec in ( SELECT nr_sequencia from pls_rec_glosa_proc where nr_seq_conta_rec in ( select nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia));
	delete FROM pls_rec_glosa_glosas_imp where nr_seq_proc_imp in ( SELECT nr_sequencia from pls_rec_glosa_proc_imp where nr_seq_conta_imp in ( select nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) ) );
	delete FROM pls_rec_glosa_proc where nr_seq_conta_rec in ( SELECT nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia);
	delete FROM pls_rec_glosa_proc_imp where nr_seq_conta_imp in ( SELECT nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) );

	delete FROM pls_rec_retorno_glosa where nr_seq_mat_imp in ( SELECT nr_sequencia from pls_rec_glosa_mat_imp where nr_seq_conta_imp in ( select nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) ) );
	delete FROM pls_rec_glosa_glosas where nr_seq_mat_rec in ( SELECT nr_sequencia from pls_rec_glosa_mat where nr_seq_conta_rec in ( select nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia));
	delete FROM pls_rec_glosa_glosas_imp where nr_seq_mat_imp in ( SELECT nr_sequencia from pls_rec_glosa_mat_imp where nr_seq_conta_imp in ( select nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) ) );
	delete FROM pls_rec_glosa_mat where nr_seq_conta_rec in ( SELECT nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia);
	delete FROM pls_rec_glosa_mat_imp where nr_seq_conta_imp in ( SELECT nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) );

	delete FROM pls_rec_glosa_anexo where nr_seq_conta_rec in ( SELECT nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia);
	delete FROM pls_rec_glosa_conta_aud where nr_seq_conta_rec in ( SELECT nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia);
	delete FROM pls_rec_retorno_glosa where nr_seq_conta_imp in ( SELECT nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) );
	delete FROM pls_rec_glosa_glosas where nr_seq_conta_rec in ( SELECT nr_sequencia from pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia);
	delete FROM pls_rec_glosa_glosas_imp where nr_seq_conta_imp in ( SELECT nr_sequencia from pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( select nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia ) );
	delete FROM pls_rec_glosa_conta where nr_seq_protocolo = r_c01_w.nr_sequencia;
	delete FROM pls_rec_glosa_conta_imp where nr_seq_protocolo_cta in ( SELECT nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia );

	delete FROM pls_rec_retorno_glosa where nr_seq_protocolo_cta in ( SELECT nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia );
	delete FROM pls_rec_glosa_glosas_imp where nr_seq_protocolo_cta in ( SELECT nr_sequencia from pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia );
	delete FROM pls_rec_glosa_prot_cta_imp where nr_seq_protocolo = r_c01_w.nr_sequencia;

	delete FROM pls_rec_glosa_protocolo where nr_sequencia = r_c01_w.nr_sequencia;

	commit;
end loop;

select	count(1)
into STRICT	contador_w
from	pls_rec_glosa_protocolo
where	nr_seq_lote = nr_seq_lote_w;

if (contador_w = 0 AND nr_seq_lote_w IS NOT NULL AND nr_seq_lote_w::text <> '') then
	delete FROM PLS_XML_ARQUIVO where NR_SEQ_LOTE_REC_GLOSA = nr_seq_lote_w;
	delete FROM PLS_REC_GLOSA_LOTE where nr_sequencia = nr_seq_lote_w;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_exclusao_recurso_glosa ( ie_tipo_p text, nr_seq_ref_p bigint, nm_usuario_p text) FROM PUBLIC;

