CREATE OR REPLACE PROCEDURE pls_executa_simulacao_preco ( nr_seq_generica_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p:

G - Gerar simulação
D - Desfazer simulação
F - Fechar simulação
R - Reabrir simulação
S - Selecionar resultado prestador
L - Limpar seleção resultado prestador

*/
BEGIN

if (nr_seq_generica_p IS NOT NULL AND nr_seq_generica_p::text <> '') then

	if (ie_opcao_p = 'G') then
		CALL pls_smp_pck.gerar_simulacao(nr_seq_generica_p, nm_usuario_p, cd_estabelecimento_p);
	elsif (ie_opcao_p = 'D') then
		CALL pls_smp_pck.desfazer_simulacao(nr_seq_generica_p);
	elsif (ie_opcao_p = 'F') then
		CALL pls_smp_pck.fechar_simulacao(nr_seq_generica_p);
	elsif (ie_opcao_p = 'R') then
		CALL pls_smp_pck.reabrir_simulacao(nr_seq_generica_p);
	elsif (ie_opcao_p = 'S') then
		CALL pls_smp_pck.selecionar_result_prest(nr_seq_generica_p);
	elsif (ie_opcao_p = 'L') then
		CALL pls_smp_pck.limpar_selecao_result_prest(nr_seq_generica_p);

	end if;

end if;

end;
$body$
