CREATE OR REPLACE PROCEDURE pls_executa_visita_tecnica ( nr_sequencia_p bigint, ie_acao_p text, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
BEGIN
if (ie_acao_p = 'L') then
	CALL pls_visita_tecnica_prest_pck.liberar_formulario(	nr_seq_formulario_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p);
end if;

if (ie_acao_p = 'I') then
	CALL pls_visita_tecnica_prest_pck.inativar_formulario(	nr_seq_formulario_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p);
end if;

if (ie_acao_p = 'D') then
	CALL pls_visita_tecnica_prest_pck.duplicar_formulario(	nr_seq_formulario_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p);
end if;

if (ie_acao_p = 'H') then
	CALL pls_visita_tecnica_prest_pck.liberar_historico(	nr_seq_historico_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p);
end if;

if (ie_acao_p = 'CA') then
	CALL pls_visita_tecnica_prest_pck.confirmar_agendamento(	nr_seq_historico_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p);
end if;

if (ie_acao_p = 'CN') then
	CALL pls_visita_tecnica_prest_pck.cancelar_agendamento(	nr_seq_historico_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p);
end if;

if (ie_acao_p = 'LV') then
	CALL pls_visita_tecnica_prest_pck.liberar_visita(	nr_seq_visita_p		=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p,
		ie_commit_p		=> ie_commit_p);
end if;

if (ie_acao_p = 'ASA') then
	CALL pls_visita_tecnica_prest_pck.alterar_situacao_auditor(	nr_seq_visita_auditor_p	=> nr_sequencia_p,
		nm_usuario_p		=> nm_usuario_p,
		ie_commit_p		=> ie_commit_p);
end if;

if (ie_acao_p = 'CPF') then
	CALL pls_visita_tecnica_prest_pck.confirmar_preenchimento_form(	nr_seq_visita_form_p		=> nr_sequencia_p,
		ie_origem_preenchimento_p	=> 'T',
		nm_usuario_p			=> nm_usuario_p,
		ie_commit_p			=> ie_commit_p);
end if;

if (ie_acao_p = 'EF') then
	CALL pls_visita_tecnica_prest_pck.excluir_visita_form(	nr_seq_visita_form_p		=> nr_sequencia_p,
		nm_usuario_p			=> nm_usuario_p,
		ie_commit_p			=> ie_commit_p);
end if;

if (ie_acao_p = 'DRI') then
	CALL pls_visita_tecnica_prest_pck.desfazer_reprov_inconsistencia(	nr_seq_incons_p			=> nr_sequencia_p,
		nm_usuario_p			=> nm_usuario_p,
		ie_commit_p			=> ie_commit_p);
end if;

if (ie_acao_p = 'DAI') then
	CALL pls_visita_tecnica_prest_pck.desfazer_aprov_inconsistencia(	nr_seq_incons_p			=> nr_sequencia_p,
		nm_usuario_p			=> nm_usuario_p,
		ie_commit_p			=> ie_commit_p);
end if;

if (ie_acao_p = 'API') then
	CALL pls_visita_tecnica_prest_pck.aprovar_prazo_solicitado(	nr_seq_historico_p		=> nr_sequencia_p,
		nm_usuario_p			=> nm_usuario_p,
		ie_commit_p			=> ie_commit_p);
end if;

if (ie_acao_p = 'RPI') then
	CALL pls_visita_tecnica_prest_pck.recusar_prazo_solicitado(	nr_seq_historico_p		=> nr_sequencia_p,
		nm_usuario_p			=> nm_usuario_p,
		ie_commit_p			=> ie_commit_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
