CREATE OR REPLACE PROCEDURE pls_exec_lote_rec_glosa_emp ( nr_seq_lote_p pls_grg_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_opcao_p text, nr_seq_grg_protocolo_p pls_grg_protocolo.nr_sequencia%type default null, nr_seq_grg_guia_p pls_grg_guia.nr_sequencia%type default null, nr_seq_grg_proc_p pls_grg_guia_proc.nr_sequencia%type default null, nr_seq_grg_mat_p pls_grg_guia_mat.nr_sequencia%type default null) AS $body$
BEGIN

if (ie_opcao_p = 'FL') then -- Fechar lote
	CALL pls_rec_glosa_emp_pck.fechar_lote_recurso( nr_seq_lote_p, nm_usuario_p);

elsif (ie_opcao_p = 'DFL') then -- Desfazer fechar
	CALL pls_rec_glosa_emp_pck.desfazer_fechar_lote_recurso( nr_seq_lote_p, nm_usuario_p);

elsif (ie_opcao_p = 'GD') then -- Gerar dados recurso de glosa
	CALL pls_rec_glosa_emp_pck.gerar_dados_rec_glosa( nr_seq_lote_p, nm_usuario_p);

elsif (ie_opcao_p = 'DGD') then -- Desfazer dados recurso de glosa
	CALL pls_rec_glosa_emp_pck.excluir_dados_rec_glosa( nr_seq_lote_p);

elsif (ie_opcao_p = 'DAP') then -- Desfazer acatar protocolo
	CALL pls_rec_glosa_emp_pck.desfazer_acatar_recursar_prot( nr_seq_grg_protocolo_p, nm_usuario_p);

elsif (ie_opcao_p = 'DAG') then -- Desfazer acatar guia
	CALL pls_rec_glosa_emp_pck.desfazer_acatar_recursar_guia( nr_seq_grg_guia_p, nm_usuario_p);

elsif (ie_opcao_p = 'DAI') then -- Desfazer acatar item
	CALL pls_rec_glosa_emp_pck.desfazer_acatar_recursar_item( nr_seq_grg_proc_p, nr_seq_grg_mat_p, nm_usuario_p);
end if;

end;
$body$
