CREATE OR REPLACE PROCEDURE pls_exec_proc_adic_versao_html ( nr_seq_proc_adic_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_retorno_w			varchar(255);
ie_processo_executado_w		pls_proc_adic_versao_log.ie_processo_executado%type;


BEGIN

-- Alteração de status para Em processamento
update	pls_processo_adic_versao
set	ie_status = '3'
where	nr_sequencia = nr_seq_proc_adic_p;

commit;

CALL pls_exec_proc_adic_versao(	nr_seq_proc_adic_p,
				nm_usuario_p);

select	coalesce(max(ie_processo_executado),'N')
into STRICT	ie_processo_executado_w
from	pls_proc_adic_versao_log
where	nr_seq_proc_adic_versao = nr_seq_proc_adic_p;

if (ie_processo_executado_w = 'S') then
	-- Alteração de status para Processamento finalizado
	update	pls_processo_adic_versao
	set	ie_status = '4'
	where	nr_sequencia = nr_seq_proc_adic_p;
else
	-- Alteração de status para Processamento com falha
	update	pls_processo_adic_versao
	set	ie_status = '5'
	where	nr_sequencia = nr_seq_proc_adic_p;
end if;

commit;

end;
$body$
