CREATE OR REPLACE PROCEDURE pls_fechar_contas_protocolo ( nr_seq_protocolo_p bigint, qt_conta_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
 
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	pls_conta	a 
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	a.ie_status		= 'L';	
 
nr_seq_conta_w		bigint;	
	 

BEGIN 
 
qt_conta_p	:= 0;
 
open C01;
loop 
fetch C01 into	 
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	CALL pls_fechar_conta(nr_seq_conta_w, 'S', null, 
			 'N', cd_estabelecimento_p, nm_usuario_p, null, null);
			 
	qt_conta_p := qt_conta_p + 1;
	 
	end;
end loop;
close C01;
 
--O Commit é inserido aqui para que somente sera realizado estas alterações caso não haja problemas em nehuma das contas do protocolo. 
Commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
