SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_fechar_lote_comissao ( nr_seq_lote_p bigint) AS $body$
DECLARE


dt_referencia_w		timestamp;
ie_lote_contabil_w	varchar(1);


BEGIN

select	max(dt_referencia)
into STRICT	dt_referencia_w
from	pls_lote_comissao
where	nr_sequencia	= nr_seq_lote_p;

ie_lote_contabil_w	:= obter_se_lote_contabil_gerado(24, dt_referencia_w);

if (ie_lote_contabil_w = 'S') then
	/* Não é possível concluir a ação executada pois existe um lote contábil gerado para a competência #@DT_COMPETENCIA#@! */

	CALL wheb_mensagem_pck.exibir_mensagem_abort(458996, 'DT_COMPETENCIA=' || to_char(dt_referencia_w,'mm/yyyy'));
end if;

update	pls_lote_comissao
set	dt_fechamento_lote = clock_timestamp(),
	ie_status = 'F' -- Fechado
where	nr_sequencia = nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
