SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_fechar_mes_competencia ( nr_seq_mes_compet_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_opcao_p text) AS $body$
DECLARE


/*	ie_opcao_p
	F - Fechar mês
	A - Abrir mês
*/
ds_maquina_w		varchar(80);
cd_estabelecimento_w	bigint;
dt_mes_competencia_w	timestamp;


BEGIN

select	substr(obter_inf_sessao(0)||' - '||obter_inf_sessao(1),1,80)
into STRICT	ds_maquina_w
;

select	cd_estabelecimento,
	dt_mes_competencia
into STRICT	cd_estabelecimento_w,
	dt_mes_competencia_w
from	pls_competencia
where	nr_sequencia = nr_seq_mes_compet_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_opcao_p = 'F') then

	update	pls_competencia
	set	nm_usuario_fechamento	= nm_usuario_p,
		dt_fechamento 		= clock_timestamp(),
		ds_maquina_fechamento 	= ds_maquina_w
	where	nr_sequencia 		= nr_seq_mes_compet_p
	and	cd_estabelecimento 	= cd_estabelecimento_p;

	insert into pls_competencia_hist(nr_sequencia,
		cd_estabelecimento,
		nr_seq_competencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_liberacao,
		ds_maquina_liberacao,
		nm_usuario_liberacao,
		dt_competencia,
		dt_fechamento,
		nm_usuario_fechamento,
		ds_maquina_fechamento)
	values (	nextval('pls_competencia_hist_seq'),
		cd_estabelecimento_w,
		nr_seq_mes_compet_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null, -- DT_LIBERACAO
		null, -- DS_MAQUINA_LIBERACAO
		null, -- NM_USUARIO_LIBERACAO
		dt_mes_competencia_w,
		clock_timestamp(),
		nm_usuario_p,
		ds_maquina_w);

	--Rotina para gerar o resultado
	--pls_gerar_margem_beneficiario(dt_mes_competencia_w, cd_estabelecimento_w, nm_usuario_p);
elsif (ie_opcao_p = 'A') then

	update	pls_competencia
	set	nm_usuario_liberacao	= nm_usuario_p,
		dt_liberacao 		= clock_timestamp(),
		ds_maquina_liberacao 	= ds_maquina_w,
		nm_usuario_fechamento 	 = NULL,
		dt_fechamento 		 = NULL
	where	nr_sequencia 		= nr_seq_mes_compet_p
	and	cd_estabelecimento 	= cd_estabelecimento_p;

	insert into pls_competencia_hist(nr_sequencia,
		cd_estabelecimento,
		nr_seq_competencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_liberacao,
		ds_maquina_liberacao,
		nm_usuario_liberacao,
		dt_competencia,
		dt_fechamento,
		nm_usuario_fechamento,
		ds_maquina_fechamento)
	values (	nextval('pls_competencia_hist_seq'),
		cd_estabelecimento_w,
		nr_seq_mes_compet_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		ds_maquina_w,
		nm_usuario_p,
		dt_mes_competencia_w,
		null, -- DT_FECHAMENTO
		null, -- NM_USUARIO_FECHAMENTO
		null); -- DS_MAQUINA_FECHAMENTO
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
