SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_finalizar_entrevista ( nr_seq_pessoa_proposta_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proposta_w		bigint;
cd_pessoa_fisica_w		varchar(10);
nr_seq_entrevista_w		bigint;


BEGIN

/* Obter dados dados da pessoa da proposta */

select	nr_seq_proposta,
	cd_beneficiario
into STRICT	nr_seq_proposta_w,
	cd_pessoa_fisica_w
from	pls_proposta_beneficiario
where	nr_sequencia	= nr_seq_pessoa_proposta_p;

/* Obter o registro de entrevista da pessoa */

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_entrevista_w
from	pls_entrevista_qualificada
where	cd_pessoa_fisica	= cd_pessoa_fisica_w
and	coalesce(dt_fim_entrevista::text, '') = '';

if (nr_seq_entrevista_w	> 0) then
	update	pls_entrevista_qualificada
	set	dt_fim_entrevista	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_entrevista_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
