SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_finalizar_evento_atend ( nr_seq_evento_p bigint, nr_seq_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w			varchar(2);
ie_status_atend_w		varchar(2);
ie_tipo_pessoa_w		varchar(2);


BEGIN

begin
	select	a.ie_status,
		CASE WHEN coalesce(b.cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END
	into STRICT	ie_status_atend_w,
		ie_tipo_pessoa_w
	from	pls_atendimento		b,
		pls_atendimento_evento	a
	where	a.nr_sequencia = nr_seq_evento_p
	and	a.nr_seq_atendimento = b.nr_sequencia;
exception
when others then
	ie_status_atend_w	:=	null;
	ie_tipo_pessoa_w	:=	null;
end;

ie_status_w	:= pls_obter_status_atendimento(0,nr_seq_evento_p,ie_status_atend_w,ie_tipo_pessoa_w);

if (ie_status_w = 'P') then
	update	pls_atendimento_evento
	set	dt_fim_evento		= clock_timestamp(),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_status		= 'P'
	where	nr_sequencia		= nr_seq_evento_p;
elsif (ie_status_w = 'G' or ie_status_w = 'C') then

	if (ie_status_atend_w = 'A') then
		update	pls_atendimento_evento
		set	dt_fim_evento		= clock_timestamp()
		where	nr_sequencia		= nr_seq_evento_p;
	end if;

	update	pls_atendimento_evento
	set	dt_conclusao		= clock_timestamp(),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ie_status		= 'C'
	where	nr_sequencia		= nr_seq_evento_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
