SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_finalizar_imp_fila_web ( nr_seq_xml_arquivo_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Finalizar o processo de importação por fila
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_situacao_w		pls_protocolo_conta.ie_situacao%type;
nr_seq_protocolo_w	pls_protocolo_conta.nr_sequencia%type;
ie_tipo_processo_w	pls_xml_arquivo.ie_tipo_processo%type := 1;


BEGIN

begin
	--Identificar o tipo de processo que esta sendo utilizado na importação 1 - Contas médicas 2 - Recurso de glosa
	select	ie_tipo_processo
	into STRICT	ie_tipo_processo_w
	from	pls_xml_arquivo
	where	nr_sequencia = nr_seq_xml_arquivo_p;

	--Processo Contas Médicas
	if ( ie_tipo_processo_w = 1 ) then
		--Protocolo de contas médica
		select  ie_situacao,
			nr_sequencia
		into STRICT	ie_situacao_w,
			nr_seq_protocolo_w
		from	pls_protocolo_conta
		where	nr_seq_xml_arquivo = nr_seq_xml_arquivo_p;
	else
		--Protocolo do Recurso de Glosa
		select  ie_situacao,
			nr_sequencia
		into STRICT	ie_situacao_w,
			nr_seq_protocolo_w
		from	pls_rec_glosa_protocolo
		where	nr_seq_xml_arquivo = nr_seq_xml_arquivo_p;
	end if;

exception
	when others then
	nr_seq_protocolo_w 	:= null;
	ie_situacao_w 		:= null;
	ie_tipo_processo_w	:= null;
end;



--Situação Importando
if ( ie_situacao_w = 'I') then
	update	pls_xml_arquivo
	set	ie_status = 'ERR',
		dt_finalizacao_arquivo = clock_timestamp(),
		ds_erro = 'Importação do arquivo XML finalizada, com o protocolo com a situação de importando.'
	where	nr_sequencia = nr_seq_xml_arquivo_p;

	--Processo Contas Médicas
	if ( ie_tipo_processo_w = 1 ) then
		CALL pls_gerar_log_erro_importacao(nr_seq_protocolo_w, 'TASY', 5002, 'Importação do arquivo XML finalizada, com o protocolo com a situação de importando.');
	end if;

elsif (nr_seq_xml_arquivo_p IS NOT NULL AND nr_seq_xml_arquivo_p::text <> '') then
		update	pls_xml_arquivo
		set	ie_status = 'FIN',
			dt_finalizacao_arquivo = clock_timestamp()
		where	nr_sequencia = nr_seq_xml_arquivo_p
		and	ie_status = 'IMP';
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_finalizar_imp_fila_web ( nr_seq_xml_arquivo_p bigint) FROM PUBLIC;

