SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_finalizar_orcamento ( nr_seq_orcamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


valor_item_proc_w		bigint;
valor_item_mat_w		bigint;


BEGIN

select 	count(1)
into STRICT	valor_item_proc_w
from	pls_orcamento_proc
where	nr_seq_orcamento = nr_seq_orcamento_p
and	vl_procedimento	> 0;

select 	count(1)
into STRICT	valor_item_mat_w
from	pls_orcamento_mat
where	nr_seq_orcamento = nr_seq_orcamento_p
and	vl_material	> 0;


if (valor_item_proc_w > 0) or (valor_item_mat_w > 0) then
	update	pls_orcamento
	set	ie_situacao	= 'F'
	where	nr_sequencia 	= nr_seq_orcamento_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort('Dever√° ser informado valor em pelo menos um item!');
end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
