CREATE OR REPLACE PROCEDURE pls_finalizar_segurado_susp ( nr_sequencia_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (coalesce(nr_sequencia_p,0) > 0) then 
 
	update	pls_segurado_suspensao 
	set	ds_motivo_fim_susp = ds_motivo_p, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp() 
	where	nr_sequencia = nr_sequencia_p;
	 
	CALL pls_atualizar_susp_atend(nr_sequencia_p, null, 'D', null,'',nm_usuario_p,'S','N',null);
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
