CREATE OR REPLACE PROCEDURE pls_finalizar_susp_mens_contr ( nr_seq_susp_mens_p bigint, dt_fim_suspensao_p timestamp, ie_cobrar_periodo_susp_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Finalizar a suspensão de cobraça de mensalidade no contrato
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
dt_inicio_suspensao_w	timestamp;


BEGIN

select	dt_inicio_suspensao
into STRICT	dt_inicio_suspensao_w
from	pls_contrato_susp_mens
where	nr_sequencia = nr_seq_susp_mens_p;

if (dt_inicio_suspensao_w > dt_fim_suspensao_p) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(274108);
	/* Mensagem: A data final de cobrança deve ser maior que a data inicial! Favor verificar. */

end if;

update	pls_contrato_susp_mens
set	dt_fim_suspensao	= dt_fim_suspensao_p,
	ie_cobrar_periodo_susp	= coalesce(ie_cobrar_periodo_susp_p,'N'),
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_susp_mens_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
