CREATE OR REPLACE PROCEDURE pls_finalizar_tempo_auditor ( nr_seq_aud_conta_grupo_p bigint, nr_seq_analise_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/*
	nr_seq_aud_conta_grupo_p =	GRUPO AUDITORIA DA ANALISE
*/
nm_auditor_atual_w 	varchar(50);
nr_seq_grupo_w		bigint;

BEGIN

begin
select 	nm_auditor_atual,
	nr_seq_grupo
into STRICT	nm_auditor_atual_w,
	nr_seq_grupo_w
from	pls_auditoria_conta_grupo
where	nr_Sequencia = nr_seq_aud_conta_grupo_p;
exception
when others then
	nm_auditor_atual_w	:= '';
	nr_seq_grupo_w		:= null;
end;

-- Cancela o tempo do auditor
update	pls_tempo_conta_grupo
set	dt_final_analise 	= clock_timestamp(),
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= 'Tasy'
where	nr_seq_auditoria	= nr_seq_aud_conta_grupo_p
and	coalesce(dt_final_analise::text, '') = '';

--desvincula auditor do grupo
update	pls_auditoria_conta_grupo
set	dt_inicio_auditor	 = NULL,
	nm_auditor_atual	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_aud_conta_grupo_p
and	coalesce(dt_liberacao::text, '') = '';

/*insere log no historico quando o auditor é desvinculado manualmente ou quando é desvinculado por um auditor que está inserindo um grupo de acesso total.*/

CALL pls_inserir_hist_analise(null, nr_seq_analise_p, 30, null, null, null, null,
				'O Auditor ' || pls_obter_nome_usuario(nm_auditor_atual_w) || ' foi desvinculado da análise do grupo atual pelo auditor ' ||
				pls_obter_nome_usuario(nm_usuario_p) || '.' || chr(13) || chr(10),
				nr_seq_grupo_w, nm_usuario_p, cd_estabelecimento_p);

CALL pls_atualizar_grupo_penden(nr_seq_analise_p, cd_estabelecimento_p, nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
