SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_finalizar_vigencia_apr ( nr_sequencia_p bigint, dt_fim_vigencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_seq_contrato_w		pls_contrato.nr_sequencia%type;
nr_seq_plano_w			pls_plano.nr_sequencia%type;
nr_seq_tabela_w			pls_tabela_preco.nr_sequencia%type;
nr_seq_classif_dependencia_w	pls_segurado.nr_seq_classif_dependencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_segurado
	where	nr_seq_contrato = nr_seq_contrato_w
	and	nr_seq_plano	= nr_seq_plano_w
	and	nr_seq_tabela	= nr_seq_tabela_w
	and	((nr_seq_classif_dependencia = nr_seq_classif_dependencia_w) or (coalesce(nr_seq_classif_dependencia_w::text, '') = ''))
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

BEGIN

select	max(nr_seq_contrato),
	max(nr_seq_plano),
	max(nr_seq_tabela),
	max(nr_seq_classif_dependencia)
into STRICT	nr_seq_contrato_w,
	nr_seq_plano_w,
	nr_seq_tabela_w,
	nr_seq_classif_dependencia_w
from	pls_regra_apropriacao
where	nr_sequencia = nr_sequencia_p;

update	pls_regra_apropriacao
set	dt_fim_vigencia = dt_fim_vigencia_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p;

for r_c01_w in c01 loop
	begin
	CALL pls_preco_beneficiario_pck.gravar_preco_benef(r_c01_w.nr_sequencia, 'AR', 'S', trunc(add_months(dt_fim_vigencia_p,1),'mm'), 'N', null, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);
	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
