CREATE OR REPLACE PROCEDURE pls_finalizar_vigencia_cp ( nr_seq_regra_p bigint, ie_tipo_regra_p text, dt_fim_vigencia_p timestamp, nm_usuario_p text) AS $body$
BEGIN
 
if (ie_tipo_regra_p = 'P') then 
