	set	dt_fim_vigencia = dt_fim_vigencia_p, 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao 	= clock_timestamp() 
	where	nr_sequencia 	= nr_seq_regra_p;
elsif (ie_tipo_regra_p = 'S') then 
	update	PLS_REGRA_PRECO_SERVICO 
	set	dt_fim_vigencia = dt_fim_vigencia_p, 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao 	= clock_timestamp() 
	where	nr_sequencia 	= nr_seq_regra_p;
end if;
 
