CREATE OR REPLACE PROCEDURE pls_finalizar_vig_preco ( nr_sequencia_p preco_amb.nr_sequencia%type, dt_final_vigencia_p preco_amb.dt_final_vigencia%type, ie_origem_regra_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* ie_origem_regra_p
	A - AMB
	T - TUSS
*/
BEGIN


-- AMB
if (ie_origem_regra_p = 'A') then

	-- grava a data de fim vigencia, se a mesma estiver null
	update	preco_amb
	set	dt_final_vigencia	= dt_final_vigencia_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p
	and	coalesce(dt_final_vigencia::text, '') = '';
end if;


-- TUSS
if (ie_origem_regra_p = 'T') then

	-- grava a data de fim vigencia, se a mesma estiver null
	update	preco_tuss
	set	dt_final_vigencia	= dt_final_vigencia_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_p
	and	coalesce(dt_final_vigencia::text, '') = '';
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
