SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_fin_imp_dma_demons_analise ( nr_seq_dma_analise_p bigint) AS $body$
DECLARE


nr_seq_cabecalho_w	pls_dma_cabecalho_imp.nr_sequencia%type;
vl_glosa_w		pls_dma_protocolo_imp.vl_glosa%type;


BEGIN

select	max(a.nr_sequencia) nr_seq_cabecalho,
	coalesce(sum(c.vl_glosa), 0)
into STRICT	nr_seq_cabecalho_w,
	vl_glosa_w
from	pls_dma_protocolo_imp	c,
	pls_dma_prestador_imp	b,
	pls_dma_cabecalho_imp	a
where	a.nr_sequencia		= b.NR_SEQ_DMA_CABECALHO
and	b.nr_sequencia		= c.nr_seq_dma_prestador
and	a.nr_seq_dma_analise	= nr_seq_dma_analise_p;

if (nr_seq_cabecalho_w IS NOT NULL AND nr_seq_cabecalho_w::text <> '') then
	update	pls_dma_cabecalho_imp
	set	vl_glosa	= vl_glosa_w
	where	nr_sequencia	= nr_seq_cabecalho_w;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
