CREATE OR REPLACE PROCEDURE pls_gerar_acoes_import_a560 ( nr_seq_nota_debito_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Gerar ações ao importar o arquivo A560, utilizado na importação do A560 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ 
 
nr_seq_ptu_fatura_w	bigint;
nr_seq_pls_fatura_w	bigint;
nr_seq_acao_w		bigint := null;
ie_gerar_tit_fat_ndr_w	varchar(1) := 'S';


BEGIN 
if (nr_seq_nota_debito_p IS NOT NULL AND nr_seq_nota_debito_p::text <> '') then 
	select	max(z.nr_seq_ptu_fatura), 
		max(z.nr_seq_pls_fatura) 
	into STRICT	nr_seq_ptu_fatura_w, 
		nr_seq_pls_fatura_w 
	from	ptu_nota_debito		x, 
		ptu_camara_contestacao	y, 
		pls_lote_contestacao	z 
	where	y.nr_sequencia	=	x.nr_seq_camara_contest 
	and	z.nr_sequencia	=	y.nr_seq_lote_contest 
	and	x.nr_sequencia	=	nr_seq_nota_debito_p;
	 
	-- Pagamento 
	if (nr_seq_ptu_fatura_w IS NOT NULL AND nr_seq_ptu_fatura_w::text <> '') then 
		nr_seq_acao_w := pls_obter_acao_intercambio(	'13',  -- Importação do A560 
						'2',  -- Gerar título a pagar 
						nr_seq_ptu_fatura_w, null, null, null, clock_timestamp(), 'A560', 'N', nr_seq_acao_w);
						 
		if (nr_seq_acao_w IS NOT NULL AND nr_seq_acao_w::text <> '') then -- Gerar titulo a pagar 
			 ie_gerar_tit_fat_ndr_w := 'N';
			CALL pls_gerar_titulo_pagar_a560( nr_seq_nota_debito_p, nr_seq_acao_w, 'N', nm_usuario_p, cd_estabelecimento_p);
		end if;	
		 
		if (ie_gerar_tit_fat_ndr_w = 'S') then 
			nr_seq_acao_w := pls_obter_acao_intercambio(	'13',  -- Importação do A560 
							'10',  -- Gerar título a pagar (Fatura) 
							nr_seq_ptu_fatura_w, null, null, null, clock_timestamp(), 'A560', 'N', nr_seq_acao_w);
							 
			if (nr_seq_acao_w IS NOT NULL AND nr_seq_acao_w::text <> '') then -- Gerar titulo a pagar (Fatura) 
				CALL pls_gerar_titulo_pagar_a560( nr_seq_nota_debito_p, nr_seq_acao_w, 'N', nm_usuario_p, cd_estabelecimento_p);
			end if;	
			 
			nr_seq_acao_w := pls_obter_acao_intercambio(	'13',  -- Importação do A560 
							'11',  -- Gerar título a pagar (NDR) 
							nr_seq_ptu_fatura_w, null, null, null, clock_timestamp(), 'A560', 'N', nr_seq_acao_w);
							 
			if (nr_seq_acao_w IS NOT NULL AND nr_seq_acao_w::text <> '') then -- Gerar titulo a pagar (NDR) 
				CALL pls_gerar_titulo_pagar_a560( nr_seq_nota_debito_p, nr_seq_acao_w, 'N', nm_usuario_p, cd_estabelecimento_p);
			end if;	
		end if;	
	end if;
	 
	-- Faturamento 
	if (nr_seq_pls_fatura_w IS NOT NULL AND nr_seq_pls_fatura_w::text <> '') then 
		nr_seq_acao_w := pls_obter_acao_intercambio(	'13',  -- Importação do A560 
						'2',  -- Gerar título a pagar 
						null, nr_seq_pls_fatura_w, null, null, clock_timestamp(), 'A560', 'N', nr_seq_acao_w);
						 
		if (nr_seq_acao_w IS NOT NULL AND nr_seq_acao_w::text <> '') then -- Gerar titulo a pagar 
			ie_gerar_tit_fat_ndr_w := 'N';
			CALL pls_gerar_titulo_pagar_a560( nr_seq_nota_debito_p, nr_seq_acao_w, 'N', nm_usuario_p, cd_estabelecimento_p);
		end if;	
		 
		if (ie_gerar_tit_fat_ndr_w = 'S') then 
			nr_seq_acao_w := pls_obter_acao_intercambio(	'13',  -- Importação do A560 
							'10',  -- Gerar título a pagar (Fatura) 
							null, nr_seq_pls_fatura_w, null, null, clock_timestamp(), 'A560', 'N', nr_seq_acao_w);
							 
			if (nr_seq_acao_w IS NOT NULL AND nr_seq_acao_w::text <> '') then -- Gerar titulo a pagar (Fatura) 
				CALL pls_gerar_titulo_pagar_a560( nr_seq_nota_debito_p, nr_seq_acao_w, 'N', nm_usuario_p, cd_estabelecimento_p);
			end if;	
			 
			nr_seq_acao_w := pls_obter_acao_intercambio(	'13',  -- Importação do A560 
							'11',  -- Gerar título a pagar (NDR) 
							null, nr_seq_pls_fatura_w, null, null, clock_timestamp(), 'A560', 'N', nr_seq_acao_w);
							 
			if (nr_seq_acao_w IS NOT NULL AND nr_seq_acao_w::text <> '') then -- Gerar titulo a pagar (NDR) 
				CALL pls_gerar_titulo_pagar_a560( nr_seq_nota_debito_p, nr_seq_acao_w, 'N', nm_usuario_p, cd_estabelecimento_p);
			end if;	
		end if;	
	end if;
commit;
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
