CREATE OR REPLACE PROCEDURE pls_gerar_acoes_import_a580 ( nr_seq_fat_geral_p ptu_fatura_geral.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_regra_tit_p pls_regra_tit_fat_geral.nr_sequencia%type, ie_apenas_vincular_tit_p text, nr_titulo_apagar_p ptu_fatura_geral.nr_titulo_pagar%type) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar ações ao importar o arquivo A580, utilizado na importação do A580
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/BEGIN

-- Apenas vincular o titulo
if (coalesce(ie_apenas_vincular_tit_p, 'N') = 'S') and (nr_titulo_apagar_p IS NOT NULL AND nr_titulo_apagar_p::text <> '') and (nr_seq_fat_geral_p IS NOT NULL AND nr_seq_fat_geral_p::text <> '') then

	CALL pls_vinc_titulo_pagar_a580(nr_seq_fat_geral_p, nr_titulo_apagar_p, nm_usuario_p);

-- Senão insere (comportamento padrão
elsif	((nr_seq_fat_geral_p IS NOT NULL AND nr_seq_fat_geral_p::text <> '') and (coalesce(ie_apenas_vincular_tit_p, 'N') = 'N')) then

	CALL pls_gerar_titulo_pagar_a580(	nr_seq_fat_geral_p, 'N', nm_usuario_p, cd_estabelecimento_p, nr_seq_regra_tit_p);
	commit;
end if;

-- varre os registros que não possuem nenhum titulo gerado e apagam eles
delete	FROM ptu_fatura_geral
where	nr_sequencia		= nr_seq_fat_geral_p
and	coalesce(nr_titulo_pagar::text, '') = ''
and	coalesce(nr_titulo_receber::text, '') = ''
and	coalesce(nr_seq_mot_cancelamento::text, '') = '';
commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
