CREATE OR REPLACE PROCEDURE pls_gerar_agenda_consulta ( nr_seq_atendimento_p bigint, nr_seq_evento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

				 
cd_cgc_outorgante_w		varchar(15);
cd_convenio_w			varchar(15);
nm_pessoa_w			varchar(80);

BEGIN
 
select	cd_cgc_outorgante 
into STRICT 	cd_cgc_outorgante_w 
from	pls_outorgante 
where	cd_estabelecimento = cd_estabelecimento_p;
 
begin 
select	cd_convenio 
into STRICT	cd_convenio_w 
from	convenio 
where	cd_cgc = cd_cgc_outorgante_w;
exception 
when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(261813,'');
	--Falta informação do convenio!' 
end;
 
select	substr(obter_nome_pf(cd_pessoa_fisica_p),1,80) 
into STRICT	nm_pessoa_w
;
 
insert into agenda_consulta(nr_sequencia, cd_convenio, dt_agenda, 
	nr_minuto_duracao, ie_status_agenda, ie_classif_agenda, 
	dt_atualizacao, nm_usuario, cd_turno, 
	cd_pessoa_fisica, nr_atend_pls, nr_seq_evento_atend, 
	nm_paciente) 
values (nextval('agenda_consulta_seq'), cd_convenio_w, clock_timestamp(), 
	60, 'O', 'T', 
	clock_timestamp(), nm_usuario_p, 0, 
	cd_pessoa_fisica_p, nr_seq_atendimento_p, nr_seq_evento_p, 
	nm_pessoa_w);
	 
commit;
 
end;
$body$
